  ks1m1 ~ beta(19,2) ;
  ks2m2 ~ beta(19,2) ;
  ds1m1 ~ normal(.0,.5) ;
  ds2m2 ~ normal(.0,.5) ;
  ds1s2 ~ normal(.0,.5) ;
  ds2s1 ~ normal(.0,.5) ;
  observation_std[1] ~ normal(.0,.1) ;
  observation_std[2] ~ normal(.0,1) ;
  observation_std[3] ~ normal(.0,.1) ;
  observation_std[4] ~ normal(.0,1) ;
  y0_s1 ~ normal(.0,.15) ;
  y0_s2 ~ normal(.0,.15) ;
  real pi2 = 2*pi() ;
  real state;
  for (n in 1:n_times){
    state = fmod(omega[n,names_indicator[n]] + pi(), pi2 ) - pi(); 
